import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { map, switchMap, tap, concatMap } from 'rxjs/operators';
import { Customer } from '../customer';
import { CustomerActions } from './customer.actions';
import { Router } from '@angular/router';

@Injectable()
export class CustomerEffects {
  private baseUrl = 'https://local.eternal.com/api/customer';
  constructor(
    private actions$: Actions,
    private http: HttpClient,
    private router: Router
  ) {}

  loadCustomers$ = createEffect(() =>
    this.actions$.pipe(
      ofType(CustomerActions.load),
      switchMap(() => this.http.get<Customer[]>(this.baseUrl)),
      map(customers => CustomerActions.loaded({ customers }))
    )
  );

  addCustomer$ = createEffect(() =>
    this.actions$.pipe(
      ofType(CustomerActions.add),
      concatMap(({ customer }) =>
        this.http.post<Customer[]>(this.baseUrl, customer)
      ),
      map(customers => CustomerActions.added({ customers })),
      tap(() => this.router.navigateByUrl('/customer'))
    )
  );

  updateCustomer$ = createEffect(() =>
    this.actions$.pipe(
      ofType(CustomerActions.update),
      concatMap(({ customer }) =>
        this.http.put<Customer[]>(this.baseUrl, customer)
      ),
      map(customers => CustomerActions.updated({ customers })),
      tap(() => this.router.navigateByUrl('/customer'))
    )
  );

  removeCustomer$ = createEffect(() =>
    this.actions$.pipe(
      ofType(CustomerActions.remove),
      concatMap(({ customer }) =>
        this.http.delete<Customer[]>(`${this.baseUrl}/${customer.id}`)
      ),
      map(customers => CustomerActions.removed({ customers })),
      tap(() => this.router.navigateByUrl('/customer'))
    )
  );
}

import { Injectable } from '@angular/core';
import { sortBy } from 'lodash-es';
import { Observable, of } from 'rxjs';
import { delay, tap, map } from 'rxjs/operators';
import { Customer } from './customer';
import { customers as originalCustomers } from './data';

@Injectable()
export class MockedHttpClient {
  private customers = originalCustomers;

  get(url: string): Observable<Customer[]> {
    return this.sortCustomers().pipe(this.logRequest('GET', url));
  }

  post(
    url: string,
    customer: Customer
  ): Observable<{ customers: Customer[]; id: number }> {
    const nextId = this.getNextId();
    this.customers.push({ ...customer, id: nextId });
    return this.sortCustomers().pipe(
      map((customers) => ({ customers, id: nextId })),
      this.logRequest('POST', url, customer)
    );
  }

  put(url: string, customer: Customer): Observable<Customer[]> {
    this.customers = this.customers.map((c) => {
      if (c.id === customer.id) {
        return customer;
      }
      return c;
    });
    return this.sortCustomers().pipe(this.logRequest('PUT', url, customer));
  }

  delete(url: string): Observable<Customer[]> {
    const id = Number(url.match(/(\d+)$/)[0]);
    this.customers = this.customers.filter((customer) => customer.id !== id);
    return this.sortCustomers().pipe(this.logRequest('DELETE', url));
  }

  private sortCustomers(): Observable<Customer[]> {
    const customers = sortBy(this.customers, 'name');
    return of(customers).pipe();
  }

  private logRequest(httpMethod: string, url: string, body?: any) {
    return (observable: Observable<any>) =>
      observable.pipe(
        delay(Math.random() * 1000),
        tap((response) => {
          console.group('Mocked Http Client');
          console.log(`${httpMethod}: ${url}`);
          if (body) {
            console.log(`Body: ${JSON.stringify(body)}`);
          }
          console.log(response);
          console.groupEnd();
        })
      );
  }

  getNextId() {
    return Math.max(...this.customers.map((customer) => customer.id)) + 1;
  }
}

